export type SVGAnimationElements =
  SVGAnimateElement
  | SVGAnimateMotionElement
  | SVGAnimateTransformElement
  | SVGMPathElement
  | SVGSetElement

export type SVGBasicShapeElements =
  SVGCircleElement
  | SVGEllipseElement
  | SVGLineElement
  | SVGPolygonElement
  | SVGPolylineElement
  | SVGRectElement

export type SVGContainerElements =
  SVGAElement
  | SVGDefsElement
  | SVGGElement
  | SVGMarkerElement
  | SVGMaskElement
  | SVGPatternElement
  | SVGElement
  | SVGSwitchElement
  | SVGSymbolElement

export type SVGDescriptiveElements =
  SVGDescElement
